mapper.select:
select distinct m.id, m.pid, m.type, m.name, m.component, m.menu_sort, m.icon, m.path, m.hidden, m.in_layout, m.auth, ifnull(m.perms,'') as perms
from sys_menu m where m.type in (1, 2)
order by m.pid, m.menu_sort

select distinct m.id, m.pid, m.type, m.name, m.component, m.menu_sort, m.icon, m.path, m.hidden, m.in_layout, m.auth, ifnull(m.perms,'') as perms from sys_menu m left join role_menu rm on m.id = rm.menu_id left join user_role urole on rm.role_id = urole.role_id left join sys_role ro on urole.role_id = ro.id where urole.user_id = #{userId}
AND m.name like concat('%', #{menu.name}, '%')
order by m.pid, m.menu_sort
